import React, { useState } from 'react';
import { Navigate, useNavigate } from 'react-router';
import './DeletePost.css'

const DeletePost = ({token, currentUser}) => {
    const blankPost = {title: '', description: '', price: ''}
    const [post, setPost] = useState(blankPost)
    const navigate = useNavigate()
    console.log('current User', currentUser)

const handleDelete = async (event) => {
        event.preventDefault()
        fetch(`https://strangers-things.herokuapp.com/api/2108-CSE-RM-WEB-PT/posts/${token}`, {
            method: "DELETE",
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
                }
            }).then(response => response.json())
            .then(result => {
                console.log(result)

            }).finally(navigate('/posts'))
            .catch(console.error);
} 
return <>
    <h1>Create a new Thingy Listing.</h1>
    <button onSubmit={handleDelete}>Delete</button>
</>
}
export default DeletePost;
