import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import { Routes, Route, Link, NavLink, BrowserRouter as Router, useNavigate, useParams, BrowserRouter, useSearchParams } from 'react-router-dom';
import './CurrentUserPosts.css';
import { DeletePost } from '.';

const CurrentUserPosts = ({currentUser, token, posts, setPosts}) => {
    console.log('current User', currentUser)
    console.log('current User token', token)

    const [currentUserPosts, setCurrentUserPosts] = useState([])
    const fetchUserData = async () => {
        console.log('fetch user token', token)
        try {
            const response = await fetch ('https://strangers-things.herokuapp.com/api/2108-CSE-RM-WEB-PT/users/me', {
              headers: {
                  'Content-Type': 'application/json',
                  'Authorization': `Bearer ${token}`
              }
          })
            const result = await response.json()
            console.log('fetchuserdata', result)
            setCurrentUserPosts(result.data.posts)
        } catch (error) {

        }
    }  
      useEffect (
          fetchUserData, []
      )
    return <>
       <div>Hello World</div>
        {
            currentUserPosts && currentUserPosts.map((post)=> {
                return  <>
                <div className='currentUserPostTitle'>{post.title}</div>
                <div className='currentUserPostDescription'>{post.description}</div>
                <DeletePost token={token} currentUser={currentUser} posts={posts} setPosts={setPosts} post_id={post._id}/>
                fetchUserData
                </>
            })
        }
    </>    
    }
export default CurrentUserPosts;
