import React, { useState } from 'react';
import { Navigate, useNavigate } from 'react-router';

const SendMessage = ({token, currentUser}) => {
    const blankMessage = {Content: ''}
    const [message, setMessage] = useState(blankMessage)
    const navigate = useNavigate()

    const handleSubmitMessage = () => {
        fetch('https://strangers-things.herokuapp.com/api/COHORT-NAME/2108-CSE-RM-WEB-PT/5e8929ddd439160017553e06/messages', {
            method: "POST",
            headers: {
                'Content-Type': 'application/json',
                'Authorization': 'Bearer TOKEN_STRING_HERE'
            },
            body: JSON.stringify({
                message: {
                content: "Do you still have this?  Would you take $10 less?"
                }
            })
            }).then(response => response.json())
            .then(result => {
                console.log(result);
            })
            .catch(console.error);
                }
    return <>
    <h1>Ask the Seller.</h1>
    <form onSubmit={handleSubmitMessage}>
        Message<input value={message.content} placeholder='Message' onChange={(event)=>{
            setPost({...post, title: event.target.value})
        }}></input>
        Description<input value={post.description} placeholder='Item Description' onChange={(event)=>{
            setPost({...post, description: event.target.value})
        }}></input>
        Price<input value={post.price} placeholder='Item price' onChange={(event)=>{
            setPost({...post, price: event.target.value})
        }}></input>
        Will Deliver<input type='checkbox' value='yes' onChange={(event)=>{
            setPost({...post, willDeliver: event.target.value})
        }}></input>
        <button type='submit'>Submit</button>
    </form>
</>


}
export default SendMessage;
