import React from 'react';

const SinglePost = ({post, children}) => {
    return (
        <div key={post._id} className='post'>
                <div className='description'>
                    <div className='postTitle'><b>{post.title}</b></div>   
                    <div className='sellerDetails'><b>Seller: </b> {post.author.username}{isLoggedIn && <NavLink to='/message' className='sendMessageButton'>Send Message</NavLink> }
                     </div>
                    <div><b>Item Description: </b><i>{post.description}</i></div>
                </div>
                <div>
                    <div>
                        <div className='price'><b>Price </b>{post.price}</div>
                    </div>
                    <div>
                        <div className='delivery'>
                            <b>Delivery </b> {post.willDeliver ? 'available' : 'not available'}
                        </div>
                    </div>
                </div>  
                {
        children
    }

            </div>
    )


}
export default SinglePost;
