import React, { useState } from 'react';
import { Navigate, useNavigate } from 'react-router';
import './SendMessage.css';

const SendMessage = ({token, currentUser}) => {
    const blankMessage = {Content: ''}
    const [message, setMessage] = useState(blankMessage)
    const navigate = useNavigate()

    const handleSubmitMessage = () => {
        fetch('https://strangers-things.herokuapp.com/api/COHORT-NAME/2108-CSE-RM-WEB-PT/5e8929ddd439160017553e06/messages', {
            method: "POST",
            headers: {
                'Content-Type': 'application/json',
                'Authorization': 'Bearer TOKEN_STRING_HERE'
            },
            body: JSON.stringify({
                message: {
                content: "Do you still have this?  Would you take $10 less?"
                }
            })
            }).then(response => response.json())
            .then(result => {
                console.log(result);
            })
            .catch(console.error);
                }
    return <>
    <h1>Ask the Seller.</h1>
    <form onSubmit={handleSubmitMessage}>
        Message Details<input className='messageField' value={message.content} placeholder='Type message here' onChange={(event)=>{
            setMessage({...message, content: event.target.value})
        }}></input>
        <button type='submit'>Send Message</button>
    </form>
</>
}
export default SendMessage;
