import React, { useState, useEffect } from 'react';
import { Navigate, useNavigate } from 'react-router';
import './DeletePost.css'

const baseURL = 'https://strangers-things.herokuapp.com/api'
const cohort = '/2108-CSE-RM-WEB-PT'
const postURL = `${baseURL}${cohort}/posts`

const EditPost = ({token, currentUser, posts, setPosts, post_id, currentUserPosts, setCurrentUserPosts}) => {
    const fetchPosts = async () => {
        try {
          const response = await fetch(postURL)
          const result = await response.json()
          setPosts(result.data.posts)


        } catch (error) {
          throw error
        }
      }
      useEffect (
          fetchPosts, []
      )

      const fetchUserData = async () => {
        console.log('fetch user token', token)
        try {
            const response = await fetch ('https://strangers-things.herokuapp.com/api/2108-CSE-RM-WEB-PT/users/me', {
              headers: {
                  'Content-Type': 'application/json',
                  'Authorization': `Bearer ${token}`
              }
          })
            const result = await response.json()
            console.log('fetchuserdata', result)
            setCurrentUserPosts(result.data.posts)
        } catch (error) {

        }
      }

const handleEdit = async (event) => {
    event.preventDefault()
    const blankPost = {title: '', description: '', price: ''}
    const [post, setPost] = useState(blankPost)

    fetch(`http://strangers-things.herokuapp.com/api/2108-CSE-RM-WEB-PT/posts/${post_id}`, {
      method: "PATCH",
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
      body: JSON.stringify({
        post: post
      })
    }).then(response => response.json())
      .then(result => {
        console.log(result);
      })
      .catch(console.error);

  }
  return <>
    {/* <h1>Edit A Post.</h1> */}
    <form 
    // onSubmit={handleEdit}
      >
        {/* Title:<input value={post.title} placeholder='Post Title' onChange={(event)=>{
            setPost({...post, title: event.target.value})
        }}></input>
        Description<input value={post.description} placeholder='Item Description' onChange={(event)=>{
            setPost({...post, description: event.target.value})
        }}></input>
        Price<input value={post.price} placeholder='Item price' onChange={(event)=>{
            setPost({...post, price: event.target.value})
        }}></input>
        Will Deliver<input type='checkbox' value='yes' onChange={(event)=>{
            setPost({...post, willDeliver: event.target.value})
        }}></input> */}
        <button type='submit'>Submit</button>
    </form>
  </>
}


export default EditPost;
