import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import { Routes, Route, Link, NavLink, BrowserRouter as Router, useNavigate, useParams, BrowserRouter } from 'react-router-dom';
import './style.css'
import {
   Posts,
   Message,
   Home,
   AccountForm,
   Profile,
   AddPost,
} from './components'

const AddPostButton = () => {
    return <>
    <button onClick={()=>{

    }
    }>Add New Post</button>
    <NavLink to='posts'>Posts</NavLink>
    </>
}

const Main = () => {
    const [token, setToken] = useState('')
    const [isLoggedIn, setIsLoggedIn] = useState(false)
    const [currentUser, setCurrentUser] = useState({})

    return (
        <>
        <div>

        <nav>
            <NavLink to='/'>Home</NavLink>
            <NavLink to='/account'>Account</NavLink> 
            <NavLink to='posts'>Posts</NavLink>
        </nav>

        <Routes>
            <Route path='/' exact element={ 
                <>
                <Home /> 
                <div className='accountSubmissionContainer'>
                    <div className='homeRegistrationContainer'>
                        <h1>Hello Stranger. </h1>
                        { !isLoggedIn && <AccountForm action='register' setToken={setToken} setIsLoggedIn={setIsLoggedIn} isLoggedIn={isLoggedIn} setCurrentUser={setCurrentUser}/> } 
                    </div>
                    <div className='homeLoginContainer'>
                        <h1>Welcome Back.</h1>
                        { !isLoggedIn && <AccountForm action='login' setToken={setToken} setIsLoggedIn={setIsLoggedIn} isLoggedIn={isLoggedIn} setCurrentUser={setCurrentUser}/> }
                    </div>
                </div>
                </>
                }/>
            <Route path='/register' exact element= { <AccountForm action='register' setToken={setToken} setIsLoggedIn={setIsLoggedIn} isLoggedIn={isLoggedIn} setCurrentUser={setCurrentUser}/> } />
            <Route path='/login' element={ <AccountForm setToken={setToken} action="login" setIsLoggedIn={setIsLoggedIn} isLoggedIn={isLoggedIn} setCurrentUser={setCurrentUser} /> } />
            <Route path='/account' exact element = { <Profile isLoggedIn={isLoggedIn} currentUser={currentUser} posts={<Posts />} /> } />
            <Route path='/posts' exact element={
                <> 
                <div className='postHeader'><h1>Thingy Listings</h1></div>
                {isLoggedIn && <AddPostButton /> } 
                <Posts />
                </> } />
            <Route path='/message' exact element={ <Message /> } />
        </Routes>
        </div>
        </>

    )

}



ReactDOM.render(
    <Router>
        <Main />
    </Router>,
    document.querySelector('#app')
)
