import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import { Routes, Route, Link, NavLink, BrowserRouter as Router, useNavigate, useParams, BrowserRouter, useSearchParams } from 'react-router-dom';
import './Posts.css'



const baseURL = 'https://strangers-things.herokuapp.com/api'
const cohort = '/2108-CSE-RM-WEB-PT'
const postURL = `${baseURL}${cohort}/posts`

const Posts = () => {
  const [posts, setPosts] = useState([])
  const [searchParams, setSearchParams] = useSearchParams()
  const searchTerm = searchParams.get('searchTerm')

  const postMatches = (post, text) => {
    text = text.toLowerCase()
    const {title, location} = vacation
    for (const field of [description, location]) {
      if(field.toLowerCase().includes(text)){
        return true;
      }
    }
  }

  const filteredVacations = searchTerm ? vacations.filter(vacation => vacationMatches(vacation, searchTerm)) : vacations



  const fetchPosts = async () => {
    try {
      const response = await fetch(postURL)
      const result = await response.json()
      setPosts(result.data.posts)

    } catch (error) {
      throw error
    }
  }
  useEffect (
      fetchPosts, []
  )
  return <>
    <input type='text' name='search' placeholder='search' value={searchTerm} onChange={(event) => {
      setSearchParams({searchTerm:event.target.value})
    }} />
    {
      posts.map((post) => {
        return (
            <div key={post._id} className='post'>
                <div className='description'>
                    <div className='postTitle'><b>{post.title}</b></div>   
                    <div><b>Seller: </b> {post.author.username}</div> <NavLink to='/message'>Send Message</NavLink>
                    <div><b>Item Description: </b><i>{post.description}</i></div>
                </div>
                <div>
                    <div>
                        <div className='price'><b>Price </b>{post.price}</div>
                    </div>
                    <div>
                        <div className='delivery'>
                            <b>Delivery </b> {post.willDeliver ? 'available' : 'not available'}
                        </div>
                    </div>
                </div>
            </div>
        )
      })
    }
  </>
}
export default Posts;
