import React, { useState } from 'react';
const addPost = ({token}) => {
    fetch('https://strangers-things.herokuapp.com/api/COHORT-NAME/posts', {
        method: "POST",
        headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
            post: {
            title: "My favorite stuffed animal",
            description: "This is a pooh doll from 1973. It has been carefully taken care of since I first got it.",
            price: "$480.00",
            willDeliver: true
            }
        })
        }).then(response => response.json())
        .then(result => {
            console.log(result);
        })
        .catch(console.error);
}

const AddPost = ({token}) => {
    const blankPost = {title: '', description: '', price: ''}
    const [post, setPost] = useState(blankPost)

const handleSubmit = async (event) => {
    event.preventDefault()
    await addPost(token, post)
    setPost(blankPost)
}
return <>
    <h1>Create a new Thingy Listing.</h1>
    <form>
        Title<input value={post.title} placeholder='Post Title' onChange={(event)=>{
            setPost({...post, title: event.target.value})
        }}></input>

    </form>
</>
}
export default AddPost;
