import { useEffect, useState } from "react";
import './Posts.css'

const baseURL = 'https://strangers-things.herokuapp.com/api'
const cohort = '/2108-CSE-RM-WEB-PT'
const postURL = `${baseURL}${cohort}/posts`

const Posts = () => {
  const [posts, setPosts] = useState([])

  const fetchPosts = async () => {
    try {
      const response = await fetch(postURL)
      const result = await response.json()
      setPosts(result.data.posts)

      console.log(typeof response)
      console.log(result)
    } catch (error) {
      throw error
    }
  }
  useEffect (
      fetchPosts, []
  )
  return <>
    {
      posts.map((post) => {
        return (
            <>
            <div key={post._id} class='post'>
            <div>{post.title}</div>   
            <div>{post.price}</div>
            <div><b>Seller:</b> {post.author.username}</div>
            <div>{post.description}</div>
            <div><b>Delivery:</b>{post.willDeliver}</div>

            <div>{post.price}</div>
            </div>
            </>
        )
      })
    }
  </>
}
export default Posts;
