            method: "POST",
            headers: {
                'Content-Type': 'application/json',
                'Authorization': 'Bearer TOKEN_STRING_HERE'
            },
            body: JSON.stringify({
                message: {
                content: "Do you still have this?  Would you take $10 less?"
                }
            })
            }).then(response => response.json())
            .then(result => {
                console.log(result);
            })
            .catch(console.error);
                }
    return (
        <button onClick={handleSubmitMessage}>Submit</button>
    )

}
