import ReactDOM from 'react-dom';
import { Routes, Route, Link, NavLink, BrowserRouter as Router, useNavigate, useParams, BrowserRouter, useSearchParams } from 'react-router-dom';
import './Posts.css'

const baseURL = 'https://strangers-things.herokuapp.com/api'
const cohort = '/2108-CSE-RM-WEB-PT'
const postURL = `${baseURL}${cohort}/posts`


const CurrentUserPosts = ({currentUser, token}) => {
    const [posts, setPosts] = useState([])
    console.log('current User', currentUser)
    console.log('current User token', token)
    const fetchPosts = async () => {
        try {
          const response = await fetch(postURL)
          const result = await response.json()
          setPosts(result.data.posts)


