import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import { Routes, Route, Link, NavLink, BrowserRouter as Router, useNavigate, useParams, BrowserRouter, useSearchParams } from 'react-router-dom';
import './Posts.css'



const baseURL = 'https://strangers-things.herokuapp.com/api'
const cohort = '/2108-CSE-RM-WEB-PT'
const postURL = `${baseURL}${cohort}/posts`

const CurrentUserPosts = () => {
    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')
    const [showCredentialsError, setShowCredentialsError] = useState(false)
    const [accountError, setAccountError] = useState('')
    const navigate = useNavigate()


    useEffect(() => {
        if (isLoggedIn) navigate('/account')
    }, [])

    const fetchUserData = async (auth_user_token) => {
        try {
            const response = await fetch ('https://strangers-things.herokuapp.com/api/2108-CSE-RM-WEB-PT/users/me', {
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${auth_user_token}`
                }
            })
            const result = await response.json()
            console.log('fetchuserdata', result)
            return result
        } catch (error) {

        }
    }

    const submitAccountInfo = async (event) => {
        try {
        event.preventDefault();
        const response = await fetch(`https://strangers-things.herokuapp.com/api/2108-CSE-RM-WEB-PT/users/${action}`, {
            method: "POST",
            headers: {
            'Content-Type': 'application/json'
            },
            body: JSON.stringify({
            user: {
                username: username,
                password: password
            }
            })
        })
        const result = await response.json()
        const auth_user_token = result.data.token
        console.log('promise', result)
        setToken(auth_user_token)
        setIsLoggedIn(true)


        const userData = await fetchUserData(auth_user_token)
        console.log('userData', userData)
        setCurrentUser(userData.data.username)
        setShowCredentialsError(false)
        navigate('/account')
        } catch (error) {
        console.log(error)
        const errorMessage = action === 'login' ? "Incorrect username and password combination." : "Username already taken."
        setAccountError(errorMessage)
        setShowCredentialsError(true)
        }
    }

    return <>
        <div className="actionHeader">{action}</div>
        <form onSubmit={submitAccountInfo}>
        <div><input type="text" value={username} placeholder="username" onChange={(event)=>{setUsername(event.target.value)}}/></div>
        <div><input type="password" value={password} placeholder="password" onChange={(event)=>{setPassword(event.target.value)}}/></div>
        <div><button type="submit">submit</button></div>
        </form>
        { showCredentialsError ? <div className="error">{accountError}</div> : null }

    </>
    }
export default CurrentUserPosts;
